import React, { useState } from 'react'


const TypeArea = () => {

    const [typed, setTyped] = useState('')


    return(
        <div>
            <h1>Type here</h1>
            <input type="text" value={typed} onChange={(e)=>{ setTyped(e.target.value) }} />
            <br />
            <div id='typedText'>
                {typed}
            </div>
        </div>
    )
}

export default TypeArea
